package chunk_test

import (
	"bytes"
	"encoding/hex"
	"io"
	"testing"

	"github.com/tombell/saga/serato/chunk"
)

func TestNewOentChunk(t *testing.T) {
	data, _ := hex.DecodeString("")
	buf := bytes.NewBuffer(data)

	hdr, err := chunk.NewHeader(buf)
	if err != nil {
		t.Error("expected NewHeader err to be nil")
	}

	oent, err := chunk.NewOentChunk(hdr, buf)
	if err != nil {
		t.Error("expected NewOentChunk err to be nil")
	}

	if oent == nil {
		t.Error("expected oent to not be nil")
	}
}

func TestNewOentChunkUnexpectedEOF(t *testing.T) {
	data, _ := hex.DecodeString("6F656E740000028F61646174000002870000000100000004000000D400000002000000CA002F0055007300650072007300")
	buf := bytes.NewBuffer(data)

	hdr, err := chunk.NewHeader(buf)
	if err != nil {
		t.Error("expected NewHeader err to be nil")
	}

	_, err = chunk.NewOentChunk(hdr, buf)
	if err != io.ErrUnexpectedEOF {
		t.Error("expected NewOentChunk err to be ErrUnexpectedEOF")
	}
}

func TestOentHeader(t *testing.T) {
	data, _ := hex.DecodeString("")
	buf := bytes.NewBuffer(data)

	hdr, err := chunk.NewHeader(buf)
	if err != nil {
		t.Error("expected NewHeader err to be nil")
	}

	oent, err := chunk.NewOentChunk(hdr, buf)
	if err != nil {
		t.Error("expected NewOentChunk err to be nil")
	}

	if oent.Header() != hdr {
		t.Error("expected header to be the same")
	}
}

func TestOentType(t *testing.T) {
	data, _ := hex.DecodeString("")
	buf := bytes.NewBuffer(data)

	hdr, err := chunk.NewHeader(buf)
	if err != nil {
		t.Error("expected NewHeader err to be nil")
	}

	oent, err := chunk.NewOentChunk(hdr, buf)
	if err != nil {
		t.Error("expected NewOentChunk err to be nil")
	}

	actual := oent.Type()
	expected := "oent"

	if actual != expected {
		t.Errorf("expected type to be %s, got %s", expected, actual)
	}
}
